package Day19_DoWhileLoop;

public class C04_DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i=0;
		
		while(i<5) {
			System.out.print(i + " "); // 0 1 2 3 4 
			i++;
		}
		
		int a=0;
		do {
			System.out.println(a + " ");
			a++;
		} while (a<5); //
		
		
		
		
		
		
		
		
	}

}
