package day16_ForLoops;

public class C02_ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 10 defa "Java Guzeldir" yaziniz.
		
		//0 dan basla 10'a kadar
		
		for (int i = 1; i <= 2; i++) {
			System.out.println(i + " java guzeldir");
		}
		
		
		
		for (int i = 1; i <=3; i++) {
			System.out.println(i + " Rabbim bana yardim et");
		}
		
		for (int i = 1; i <=4; i++) {
			System.out.print(i + " ");  // sadece (i) yazarsak prinln'e 1'den 4'e kadar yazdirir.
		}                               // (i + " ") yazarsak 1 2 3 4  diye ARALIKLI yazdirir.
		
		
	
		
		
					
		
	
		
			
		
		
		
		
	}
	
	

}
